import React, { useState, Component } from "react";
import ReactDom from "react-dom";
import Slider from "react-slick/lib/slider";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import classes from "./questionsCarousel.module.css";
import Modal from "./Modal";

import Button from "./UI/Button";

function QuestionsCarousel(props) {
  const [key, setNewKey] = useState(59);

  const [modalShown, setModalShown] = useState(false);

  const ChangingKey = () => {
    setNewKey((key) => {
      return (key = key + 1);
    });
    console.log(key);
  };

  let settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: false,
  };

  const Questions = props.data.map((item) => {
    return (
      <div key={item.id}>
        <p>{item.question}</p>
      </div>
    );
  });

  console.log(Questions);

  return (
    <div>
      <h2> Single Item</h2>
      {ReactDom.createPortal(
        <Modal answer={props.data} key={key} />,
        document.getElementById("modal")
      )}
      <Slider {...settings}>{Questions}</Slider>
      <Button type="button" onClick={ChangingKey}>
        Show Answer {Questions[0].key}
      </Button>
    </div>
  );
}

export default QuestionsCarousel;
