import React, { useState } from "react";
import ReactDom from "react-dom";
import Slider from "react-slick/lib/slider";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import classes from "./questions.module.css";
import Modal from "./Modal";
import Card from "./UI/Card";

import Button from "./UI/Button";

function Questions(props) {
  const [indexQuestion, setIndexQuestion] = useState(0);

  const ChangeQuestion = () => {
    /*   console.log(indexQuestion);
    return setIndexQuestion(indexQuestion + 1); */

    return setIndexQuestion((indexQuestion) => {
      console.log(indexQuestion);
      if (indexQuestion < props.data.length) {
        indexQuestion++;
      } else {
        return;
      }
    });
  };

  return (
    <div>
      <h2> Single Item</h2>
      {/*   {ReactDom.createPortal(
        <Modal answer={props.data} />,
        document.getElementById("modal")
      )} */}
      <div>
        <h3>{props.data[indexQuestion].question}</h3>
      </div>
      <Button type="button" onClick={ChangeQuestion}>
        Answer to question {props.data[indexQuestion].id}
      </Button>
    </div>
  );
}

export default Questions;
