import React, { Component } from "react";
import ReactDom from "react-dom";
import Slider from "react-slick/lib/slider";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import classes from "./questionsCarousel.module.css";
import Modal from "./Modal";

import Button from "./UI/Button";

function QuestionsCarousel(props) {
  let settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: false,
  };

  const QA = props.data.map((item) => {
    return (
      <div>
        <p>{item.question}</p>
      </div>
    );
  });

  console.log(QA);

  return (
    <div>
      <h2> Single Item</h2>
      {ReactDom.createPortal(<Modal />, document.getElementById("modal"))}
      <Slider {...settings}>{QA}</Slider>
      <Button type="button">Show Answer</Button>
    </div>
  );
}

export default QuestionsCarousel;
